<script>module.exports={data:function data(){return{filter:"",formActualizar:!1,ids:-1,nombress:"",materias:{loading:!1,columns:[{name:"nombres",field:"nombres",label:"Materia"}],data:[]}}},created:function created(){this.listaCtlgMaterias(),this.init()},methods:{excel:function excel(){this.report("Reporte Materias ",this.materias.columns,this.materias.data)},editarMateria:function editarMateria(a){this.ids=a.id,this.nombress=a.nombres,this.formActualizar=!0},guardar:function guardar(a){var b=this;null!==this.nombress&&""!==this.nombress?_.rput("materias/actualizar_ctlg_materias/".concat(this.ids),null,{ids:a.id,nombress:this.nombress}).then(function(){b.formActualizar=!1,b.listaCtlgMaterias(),b.$q.notify({type:"positive",message:"Actualizado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al actualizar"})})["finally"](function(){b.$q.loading.hide()}):this.$q.notify({type:"negative",message:"No se puede actualizar"})},eliminar:function eliminar(a){var b=this;this.ids=a.id,this.$q.loading.show(),_.rdelete("materias/eliminar_ctlg_materias/".concat(this.ids),null,{ids:a.id}).then(function(){b.listaCtlgMaterias(),b.$q.notify({type:"positive",message:"Eliminado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al eliminar"})})["finally"](function(){b.$q.loading.hide()})},nuevoMateria:function nuevoMateria(){this.modal("ctlg_materia",{})},listaCtlgMaterias:function listaCtlgMaterias(){var a=this;this.materias.loading=!0,_.rget("materias/lista_ctlg_materias").then(function(b){a.materias.data=b.data})["finally"](function(){a.materias.loading=!1})}}};</script> <style></style> <template> <q-page class="q-pa-md"> <br /> <q-table class="q-pa-md" title="Materias" :data="materias.data" :columns="materias.columns" row-key="id" :filter="filter" :loading="materias.loading" > <template v-slot:top-right> <div class="q-gutter-md row"> <q-btn color="primary" icon-right="add" label="Nueva Materia" no-caps @click="nuevoMateria" ></q-btn> <q-btn color="teal" icon="save_alt" @click="excel"> <q-tooltip> Exportar a Excel </q-tooltip> </q-btn> <q-input filled borderless dense debounce="300" v-model="filter" placeholder="Buscar" > <template v-slot:append> <q-icon name="search" /> </template> </q-input> </div> </template> <template v-slot:header="props"> <br /> <q-tr text-align: center bgcolor="orange" :props="props"> <q-th auto-width style="text-align: center" v-for="col in props.cols" :key="col.name" :props="props" > {{ col.label }} </q-th> <q-th auto-width>Acci√≥n</q-th> </q-tr> </template> <template v-slot:body="props"> <q-tr :props="props"> <q-td style="text-align: center" v-for="col in props.cols" :key="col.name" :props="props" > {{ col.value }} </q-td> <q-td auto-width style="text-align: center"> <span v-if="formActualizar && ids == props.row.id"> <q-btn color="primary" round dense icon="check_circle" @click="guardar" > </q-btn> <q-btn color="negative" round dense icon="close" @click="formActualizar = false" ></q-btn> </span> <span v-else> <q-btn color="primary" round dense icon="edit" @click="editarMateria(props.row)" ></q-btn> <q-btn color="negative" round dense icon="delete" @click="eliminar(props.row)" ></q-btn> </span> </q-td> </q-tr> <q-tr v-show="formActualizar && ids == props.row.id" :props="props" > <q-td colspan="100%"> <q-input v-model="nombress" label="Nombre"> </q-input> </q-td> </q-tr> </template> <template v-slot:loading> <q-inner-loading showing color="primary"></q-inner-loading> </template> </q-table> </q-page></template>