<script>module.exports={data:function data(){return{filter:"",formActualizar:!1,ids:-1,materiass:" ",tw:"",options:{mate:[]},cursos_materias:{loading:!1,columns:[{name:"cursos",field:"cursos",label:"Curso"},{name:"materias",field:"materias",label:"Materia"},{name:"ano_lectivo",field:"ano_lectivo",label:"A\xF1o Lectivo"}],data:[]}}},created:function created(){this.listaCursosMaterias(),this.init()},methods:{excel:function excel(){this.report("Cursos Materias",this.cursos_materias.columns,this.cursos_materias.data)},editarCursoMateria:function editarCursoMateria(a){this.tw=a.ano_lectivo,this.mate(),this.ids=a.id,this.materiass=a.materias,this.formActualizar=!0},mate:function mate(){var a=this;return _.rget("cursos_materias/materias_anos",{ano_lectivo:this.tw}).then(function(b){a.options.mate=b.data})},guardar:function guardar(a){var b=this;null!==this.materiass&&""!==this.materiass?_.rput("cursos_materias/".concat(this.ids),null,{ids:a.id,materiass:this.materiass}).then(function(){b.formActualizar=!1,b.listaCursosMaterias(),b.$q.notify({type:"positive",message:"Actualizado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al actualizar"})})["finally"](function(){b.$q.loading.hide()}):this.$q.notify({type:"negative",message:"No se puede actualizar"})},eliminar:function eliminar(a){var b=this;this.ids=a.id,this.$q.loading.show(),_.rdelete("cursos_materias/elim/".concat(this.ids),null,{ids:a.id}).then(function(){b.listaCursosMaterias(),b.$q.notify({type:"positive",message:"Eliminado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al eliminar"})})["finally"](function(){b.$q.loading.hide()})},nuevoCursoMateria:function nuevoCursoMateria(){this.modal("curso_materia",{})},listaCursosMaterias:function listaCursosMaterias(){var a=this;this.cursos_materias.loading=!0,_.rget("cursos_materias").then(function(b){a.cursos_materias.data=b.data})["finally"](function(){a.cursos_materias.loading=!1})}}};</script> <style></style> <template> <q-page class="q-pa-md"> <br /> <q-table class="q-pa-md" title="Asignar Materia al Curso" :data="cursos_materias.data" :columns="cursos_materias.columns" row-key="id" :filter="filter" :loading="cursos_materias.loading" > <template v-slot:top-right> <div class="q-gutter-md row"> <q-btn color="primary" icon-right="add" label="Nuevo Asignación" no-caps @click="nuevoCursoMateria" ></q-btn> <q-btn color="teal" icon="save_alt" @click="excel"> <q-tooltip> Exportar a Excel </q-tooltip> </q-btn> <q-input filled borderless dense debounce="300" v-model="filter" placeholder="Buscar" > <template v-slot:append> <q-icon name="search" /> </template> </q-input> </div> </template> <template v-slot:header="props"> <br /> <q-tr text-align: center bgcolor="orange" :props="props"> <q-th auto-width style="text-align: center" v-for="col in props.cols" :key="col.name" :props="props" > {{ col.label }} </q-th> <q-th auto-width>Acción</q-th> </q-tr> </template> <template v-slot:body="props"> <q-tr :props="props"> <q-td style="text-align: center" v-for="col in props.cols" :key="col.name" :props="props" > {{ col.value }} </q-td> <q-td auto-width style="text-align: center"> <span v-if="formActualizar && ids == props.row.id"> <q-btn color="primary" round dense icon="check_circle" @click="guardar" > </q-btn> <q-btn color="negative" round dense icon="close" @click="formActualizar = false" ></q-btn> </span> <span v-else> <q-btn color="primary" round dense icon="edit" @click="editarCursoMateria(props.row)" ></q-btn> <q-btn color="negative" round dense icon="delete" @click="eliminar(props.row)" ></q-btn> </span> </q-td> </q-tr> <q-tr v-show=" formActualizar && ids == props.row.id && tw == props.row.ano_lectivo " :props="props" > <q-td colspan="100%"> <q-select v-model="materiass" :options="options.mate" label="Materias" emit-value map-options > </q-select> </q-td> </q-tr> </template> <template v-slot:loading> <q-inner-loading