<script>module.exports={data:function data(){return{filter:"",formActualizar:!1,ids:-1,id_ctlg_jornadass:" ",options:{jorna:[]},jornadas:{loading:!1,columns:[{name:"nombre",field:"nombre",label:"Jornada"},{name:"ano_lectivo",field:"ano_lectivo",label:"A\xF1o Lectivo"}],data:[]}}},created:function created(){var a=this;this.listaJornadas(),Promise.all([this.jorna()]).then(function(){a.init()})},methods:{excel:function excel(){this.report("Reporte Jornadas ",this.jornadas.columns,this.jornadas.data)},jorna:function jorna(){var a=this;return _.rget("jornadas/ctlg").then(function(b){a.options.jorna=b.data})},editarJornada:function editarJornada(a){this.ids=a.id,this.id_ctlg_jornadass=a.nombre,this.formActualizar=!0},guardar:function guardar(a){var b=this;null!==this.id_ctlg_jornadass&&""!==this.id_ctlg_jornadass?_.rput("jornadas/".concat(this.ids),null,{ids:a.id,id_ctlg_jornadass:this.id_ctlg_jornadass}).then(function(){b.formActualizar=!1,b.listaJornadas(),b.$q.notify({type:"positive",message:"Actualizado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al actualizar"})})["finally"](function(){b.$q.loading.hide()}):this.$q.notify({type:"negative",message:"No se puede actualizar"})},eliminar:function eliminar(a){var b=this;this.ids=a.id,this.$q.loading.show(),_.rdelete("jornadas/elim/".concat(this.ids),null,{ids:a.id}).then(function(){b.listaJornadas(),b.$q.notify({type:"positive",message:"Eliminado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al eliminar"})})["finally"](function(){b.$q.loading.hide()})},nuevoJornada:function nuevoJornada(){this.modal("jornada",{})},listaJornadas:function listaJornadas(){var a=this;this.jornadas.loading=!0,_.rget("jornadas").then(function(b){a.jornadas.data=b.data})["finally"](function(){a.jornadas.loading=!1})}}};</script> <style></style> <template> <q-page class="q-pa-md"> <br /> <q-table class="q-pa-md" title