<script>module.exports={data:function data(){return{filter:"",formActualizar:!1,ids:-1,id_ctlg_materiass:" ",options:{mate:[]},materias:{loading:!1,columns:[{name:"materias",field:"materias",label:"Materia"},{name:"ano_lectivo",field:"ano_lectivo",label:"A\xF1o Lectivo"}],data:[]}}},created:function created(){var a=this;this.listaMaterias(),Promise.all([this.mate()]).then(function(){a.init()})},methods:{excel:function excel(){this.report("Reporte Materias ",this.materias.columns,this.materias.data)},mate:function mate(){var a=this;return _.rget("materias/ctlg").then(function(b){a.options.mate=b.data})},editarMateria:function editarMateria(a){this.ids=a.id,this.id_ctlg_materiass=a.materias,this.formActualizar=!0},guardar:function guardar(a){var b=this;null!==this.id_ctlg_materiass&&""!==this.id_ctlg_materiass?_.rput("materias/".concat(this.ids),null,{ids:a.id,id_ctlg_materiass:this.id_ctlg_materiass}).then(function(){b.formActualizar=!1,b.listaMaterias()})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al guardar"})})["finally"](function(){b.$q.loading.hide()}):this.$q.notify({type:"negative",message:"no se puede guardar"})},eliminar:function eliminar(a){var b=this;this.ids=a.id,this.$q.loading.show(),_.rdelete("materias/elim/".concat(this.ids),null,{ids:a.id}).then(function(){b.listaMaterias()})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al eliminar"})})["finally"](function(){b.$q.loading.hide()})},nuevoMateria:function nuevoMateria(){this.modal("materia",{})},listaMaterias:function listaMaterias(){var a=this;this.materias.loading=!0,_.rget("materias").then(function(b){a.materias.data=b.data})["finally"](function(){a.materias.loading=!1})}}};</script> <style></style> <template> <q-page class="q-pa-md"> <br /> <q-table class="q-pa-md" title="Materias" :data="materias.data" :columns="materias.columns" row-key="id" :filter="filter" :loading="materias.loading" > <template v-slot:top-right> <di