<script>module.exports={data:function data(){return{filter:"",formActualizar:!1,ids:-1,ida:-1,nombress:" ",paralelos:{loading:!1,columns:[{name:"paralelos",field:"paralelos",label:"Paralelo"},{name:"jornada",field:"jornada",label:"Jornada"},{name:"ano_lectivo",field:"ano_lectivo",label:"A\xF1o Lectivo"}],data:[]}}},filters:{capitalize:function capitalize(a){return a?(a=a.toString(),a.charAt(0).toUpperCase()+a.slice(1)):""},upper:function upper(a){return a?(a=a.toString(),a.toUpperCase()):""}},created:function created(){this.listaParalelos(),this.init()},methods:{excel:function excel(){this.report("Reporte Paralelos ",this.paralelos.columns,this.paralelos.data)},editarParalelo:function editarParalelo(a){this.ids=a.id,this.nombress=a.paralelos,this.formActualizar=!0},guardar:function guardar(a){var b=this;null!==this.nombress&&""!==this.nombress?_.rput("paralelos/".concat(this.ids),null,{ids:a.id,nombress:this.nombress}).then(function(){b.formActualizar=!1,b.listaParalelos(),b.$q.notify({type:"positive",message:"Actualizado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al actualizar"})})["finally"](function(){b.$q.loading.hide()}):this.$q.notify({type:"negative",message:"No se puede actualizar"})},eliminar:function eliminar(a){var b=this;this.ida=a.idpj,this.$q.loading.show(),_.rdelete("paralelos/elim/".concat(this.ida),null,{ida:a.idpj}).then(function(){b.listaParalelos(),b.$q.notify({type:"positive",message:"Eliminado con exito",timeout:500})})["catch"](function(a){b.$q.notify({type:"negative",message:a.message||"Hubo un error al eliminar"})})["finally"](function(){b.$q.loading.hide()})},nuevoParalelo:function nuevoParalelo(){this.modal("paralelo",{})},nuevoJornada:function nuevoJornada(){this.modal("paralelo_jornada",{})},listaParalelos:function listaParalelos(){var a=this;this.paralelos.loading=!0,_.rget("paralelos").then(function(b){a.paralelos.data=b.data})["finally"](function(){a.paralelos.loading=!1})}}};</script> <style></style> <template> <q-page class="q-pa-md"> <br /> <q-table class="q-pa-md q-pa-sd" title="Paralelos" :data="paralelos.data" :columns="paralelos.columns" row-key="id" :filter="filter" :loading="paralelos.loading" > <template v-slot:top-right> <div class="q-gutter-md row"> <q-btn color="primary" icon-right="add" label="Nuevo Paralelo" no-caps @click="nuevoParalelo" ></q-btn> <q-btn color="primary" icon-right="add" label="Paralelos-Jornadas" no-caps @click="nuevoJornada" ></q-btn> <q-btn color="teal" icon="save_alt" @click="excel"> <q-tooltip> Exportar a Excel </q-tooltip> </q-btn> <q-input filled borderless dense debounce="300" v-model="filter" placeholder="Search" > <template v-slot:append> <q-icon name="search" /> </template> </q-input> </div> </template> <template v-slot:header="props"> <br /> <q-tr text-align: center bgcolor="orange" :props="props"> <q-th auto-width style="text-align: center" v-for="col in props.cols" :key="col.name" :props="props" > {{ col.label }} </q-th> <q-th auto-width>Acci√≥n</q-th> </q-tr> </template> <template v-slot:body="props"> <q-tr :props="props"> <q-td style="text-align: center" v-for="col in props.cols" :key="col.name" :props="props" > {{ col.value | upper }} </q-td> <q-td auto-width style="text-align: center"> <span v-if="formActualizar && ids == props.row.id"> <q-btn color="primary" round dense icon="check_circle" @click="guardar" > </q-btn> <q-btn color="negative" round dense icon="close" @click="formActualizar = false" ></q-btn> </span> <span v-else> <q-btn color="primary" round dense icon="edit" @click="editarParalelo(props.row)" ></q-btn> <q-btn color="negative" round dense icon="delete" @click=" eliminar(props.row) && ida == props.row.idpj " ></q-btn> </span> </q-td> </q-tr> <q-tr v-show="formActualizar && ids == props.row.id" :props="props" > <q-td colspan="100%"> <q-input v-model="nombress" label="Paralelos"> </q-input> <!-- <q-select  v-model="jornadass"  :options="options.jorna"  label="jornada